        var repJson=JSON.parse(resp);
	  //获取日间系统返回的状态码 0为成功  其他是失败
        var retCode=repJson['code'];
          //成功的处理逻辑
	   if(retCode==0||retCode=='0'){		



		//根据规则转换成日间.NET版本需要的json对象
    //  var jsonData=JSON.parse(WinningJoltUtil.transform(repJson,spec));

