     //拼装最后的xml返回参数
    //  var respXML=   WinningCommonUtil.handleRetXmlData('',true);
	 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'100','成功')      
      responseMap.put('ACK',esbResponseStr);

	}else  if(retCode==101||retCode=='101'){	
			//日间处理报错
	      var errMsg=repJson['message']	
	    	 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'101',errMsg)      
	      responseMap.put('ACK',esbResponseStr);

	}else {
			//日间处理报错
	      var errMsg=repJson['message'];
    		 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'200','失败：'+errMsg)      
          responseMap.put('ACK',esbResponseStr);

		}

        }catch(e){
	      //日间处理报错
	      var errMsg=repJson['message'];
    		 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'200','失败：'+e)      
           responseMap.put('ACK',esbResponseStr);


        }

