var tempRespose=JSON.parse(resposeInfo);

//{
//    "Response": {
//        "Head": {
//            "Version": "1.1",
//            "TransferType": "Async",
//            "AckCode": "100",
//            "AckMessage": "调用成功！",
//            "ContentType": "text/json;charset=UTF-8",
//            "ContentEncoding": "",
//            "OrgId": "H0001",
//            "AppId": "S003",
//            "RecOrgId": "H0001",
//            "RecAppId": "",
//            "MessageId": "6F9619FF-8B86-D011-B66D-00C04FC96633",
//            "Timestamp": "2022-09-01 14:08:25.768"
//        },
//        "Body": ""
//    }
//}


try{
      var ackCode= tempRespose['Response']['Head']['AckCode']; 
     if('100'==ackCode||'100.2'==ackCode||'100.1'==ackCode){      
       var output=Maps.map().add('status',true).add('msg','手术申请成功').add('count',1).add('data',null);	
       msg= JsonUtils.toJsonString(output); 
       responseMap.put('ACK',msg);
     }else{     	      
       var msgCode= tempRespose['Response']['Head']['AckCode'];
       var msgInfo= tempRespose['Response']['Head']['AckMessage'];
       var errorMessage='操作失败:'+msgInfo+',消息代码:'+msgCode;
       var output=Maps.map().add('status',false).add('msg',errorMessage).add('count',1).add('data',null);	
       msg= JsonUtils.toJsonString(output); 
       responseMap.put('ACK',msg);
    }

}catch(e){
      //logger.error('手术申请异常===='+e);
      var output=Maps.map().add('status',false).add('msg','查询异常'+e);	        
      var msg= JsonUtils.toJsonString(output); 
      responseMap.put('ACK',msg);      
}

