       msg= JsonUtils.toJsonString(output); 
       responseMap.put('ACK',msg);
     }else{     	      
       var msgCode= tempRespose['Response']['Head']['AckCode'];
       var msgInfo= tempRespose['Response']['Head']['AckMessage'];
        var traceid= tempRespose['traceid'];
       var errorMessage='操作失败:'+msgInfo+',消息代码:'+msgCode+",traceid=:"+traceid;
       var output=Maps.map().add('status',false).add('msg',errorMessage).add('count',1).add('data',nul);	
       msg= JsonUtils.toJsonString(output); 
       responseMap.put('ACK',msg);
    }

}catch(e){

      var AckMessage=tempRespose['Response']['Head']['AckMessage']; 
