//logger.info('患者取消入区的请求jsonMessage ='+jsonMessage);
// 需要转换的格式要求

var spec=[
  {
    "operation": "shift",
    "spec": {
      "Demography": {

        "PatientName": "patientName",
        "PatientIdentifierList": {
          "*": {
            "IDType": {
              // 患者ID
              "PatientID": {
                "@(2,IDNumber)": "patientId"
              },
              // 病历号
              "MedicalRecordNo": {
                "@(2,IDNumber)": ["inHospitalMedicalNo"]
              } 
            }
          }
        } 

      },
      //就诊记录
      "PatientVisit": {

     //   "HandleList": {
      //    "0": {
      //      // 出区日期
      //      "HandleTime": "inpatientWardOutDate"
      //    }
     //   },
        //就诊流水号,---首页序号
        "VisitNumber": "inHospitalEncounterId"

      }
    }
  },
  {
    "operation": "default",
    "spec": {
      //取消入院
      "businessType": "1",
      "companyIdentifier": "winex"
    }
  }
];



//通过规则转换进行请求参数的处理
var jsonData=JSON.parse(WinningJoltUtil.transform(JSON.parse(jsonMessage),spec));
//把winex60的时间格式进行转换成标准格式给日间
//var convertedStr =WinningCommonUtil.handleJsonTimeFormat(JSON.stringify(jsonData), 'inpatientInDate,inpatientWardInDate', 'yyyyMMddHHmmss', 'yyyy-MM-dd HH:mm:ss');

//logger.info('患者取消入区的jsonData ='+JSON.stringify(jsonData));

//调用日间手术管理系统的json字符串
//channelMap.put('jsonData',JSON.stringify(jsonData));
channelMap.put('jsonData',JSON.stringify(jsonData));

importPackage(Packages.java.util);
importPackage(Packages.java.lang);
importPackage(Packages.com.winning.mirth);
importPackage(Packages.com.bazaarvoice.jolt);

//获取ESB推送的原始数据，进行返回结果拼装准备 originalRequestStr


var originalRequestStr=connectorMessage.getRawData();

var originalJsonObj=JSON.parse(originalRequestStr);

//去除body，保留head数据
delete originalJsonObj.Request.Body;
delete originalJsonObj.Request.Head.LicUserId;
delete originalJsonObj.Request.Head['X-LOGGER'];
delete originalJsonObj.Request.Head['X-LOGGER-EVENT'];

var requestRootStr=JSON.stringify(originalJsonObj)

         //日间返回的json数据
        var resp=response.getMessage(); 
      //   logger.info('respose writeAppointmentAdmission='+resp);


    try{

        // 转换日间系统返回的json对象
        var repJson=JSON.parse(resp);
	  //获取日间系统返回的状态码 0为成功  其他是失败
        var retCode=repJson['code'];
          //成功的处理逻辑
	   if(retCode==0||retCode=='0'){		



		//根据规则转换成日间.NET版本需要的json对象
    //  var jsonData=JSON.parse(WinningJoltUtil.transform(repJson,spec));

	  //json字符串转xml字符串
      //var retXmlStr=  WinningJoltUtil.toXml(JSON.stringify(jsonData));

      //logger.info('respose retXmlStr='+retXmlStr);

     //拼装最后的xml返回参数
    //  var respXML=   WinningCommonUtil.handleRetXmlData('',true);
	 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'100','成功')      
      responseMap.put('ACK',esbResponseStr);

	}else  if(retCode==101||retCode=='101'){	
			//日间处理报错
	      var errMsg=repJson['message']	
	    	 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'101',errMsg)      
	      responseMap.put('ACK',esbResponseStr);

	}else {
			//日间处理报错
	      var errMsg=repJson['message'];
    		 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'200','失败：'+errMsg)      
          responseMap.put('ACK',esbResponseStr);

		}

        }catch(e){
	      //日间处理报错
	      var errMsg=repJson['message'];
    		 var esbResponseStr=WinexEsbUtils.buildSurgeryResponse(requestRootStr,'200','失败：'+e)      
           responseMap.put('ACK',esbResponseStr);


        }

