        "HandleList": {
          "0": {
            // 出区日期
            "HandleTime": ["inpatientWardOutDate","operatorDate"],
          }
        },
        //就诊流水号,---首页序号
        "VisitNumber": ["inHospitalEncounterId"],
        //住院次数
        "VisitCount": "hospitalizationNumber",
        "VisitStatus": {
          "Identifier": "VisitStatusCode"
        }
      }
    }
  },
