     WinningSpUtils.callSpsHasOutParameter(hisApiConfig, procName, outArgInfo, inArgInfo) 
     var result= outArgInfo['result'];

     if('T'==result){

       var output=Maps.map().add('status',true).add('msg','操作成功').add('count',1).add('data',true);	
       msg= JsonUtils.toJsonString(output); 
        responseMap.put('ACK',msg);
     }else{

            var errMsg= outArgInfo['msg'];
            var output=Maps.map().add('status',false).add('msg','操作失败'+errMsg).add('count',1).add('data',false);	
         msg= JsonUtils.toJsonString(output); 
          responseMap.put('ACK',msg);
    }





