                "patientId": "",
                "outpatientId": "",
                "outpatientMedicalNo": "",
                "inHospitalMedicalNo": "",
                "startTime": "",
                "endTime": ""
            }
        }
    ]

    //通过规则转换进行请求参数的处理
    var jsonData = JSON.parse(WinningJoltUtil.transform(JSON.parse(requestStr), spec));

    //存储过程名称[usp_asmc_getReportByPatid]
    var procName = 'usp_asmc_getReportByPatid';
    //存储过程的参数拼接
    var inArgInfo = WinningCommonUtil.toMap(JSON.stringify(jsonData));


    var patientId = inArgInfo['patientId'];

    if (StringUtils.isEmpty(patientId)) {

        var output = Maps.map().add('status', false).add('msg', '患者[patientId]不能为空');
        var msg = JsonUtils.toJsonString(output);
        responseMap.put('ACK', msg);
    } else {

        //    logger.info('获取检查检验报告申请单信息调用请求的参数是 inArgInfo===='+inArgInfo);
        var list = WinningSpUtils.callSpsGetList(hisApiConfig, procName, inArgInfo);

        //list json字符串
        var retJsonStr = WinningCommonUtil.listToJsonStr(list);
        //logger.info('获取检查检验报告申请单存储过程usp_asmc_getReportByPatid信息返回数据是retJsonStr===='+retJsonStr);     
        var output = Maps.map().add('status', true).add('data', JSON.parse(retJsonStr));

        //若需要把数据库字段转换字段，执行下面代码,
        var repsoneSpec = [{
                "operation": "shift",
                "spec": {
                    "data": {
                        "*": {
                            "patientId": "data[&1].patientId",
                            "surveyingItemCode": "data[&1].surveyingItemCode",
                            "surveyingItemName": "data[&1].surveyingItemName",
                            "surveyingTypeCode": "data[&1].surveyingTypeCode",
                            "surveyingReportStatusDict": "data[&1].surveyingReportStatusDict",
                            "surveyingReportTime": "data[&1].surveyingReportTime",
                            "surveyingEndTime": "data[&1].surveyingEndTime",
                            "reportApplicationNumber": "data[&1].reportApplicationNumber",
                            "patientName": "data[&1].patientName",
                            "applyOpeningTime": "data[&1].applyOpeningTime",
                            "reportUrl": "data[&1].reportUrl"
                        }
                    }
                }
            },
            {
                "operation": "default",
                "spec": {
                    "data[]": {
                        "*": {
                            "patientId": "",
                            "surveyingItemCode": "",
                            "surveyingItemName": "",
                            "surveyingTypeCode": "",
                            "surveyingReportStatusDict": "",
                            "surveyingReportTime": "",
                            "surveyingEndTime": "",
                            "reportApplicationNumber": "",
                            "patientName": "",
                            "applyOpeningTime": "",
                            "reportUrl": "",
                            "statusDict": "1"
                        }
                    }
                }
            }
        ];

        // 如果需要转换则使用固定格式处理
        var retOutput = JSON.parse(WinningJoltUtil.transform(output, repsoneSpec));
        //logger.info('获取检查检验报告申请单信息返回数据是retOutput===='+retOutput);  
        retOutput.count = retOutput.data.length;
        retOutput.status = true;
        retOutput.msg = '数据获取成功';

        msg = JsonUtils.toJsonString(retOutput);
        responseMap.put('ACK', msg);
    }

} catch (e) {

    // logger.error('调用存储过程usp_asmc_getReportByPatid异常===='+e);
    var output = Maps.map().add('status', false).add('msg', '查询异常' + e);
    var msg = JsonUtils.toJsonString(output);
    responseMap.put('ACK', msg);
}

