importPackage(Packages.java.util);
importPackage(Packages.org.apache.commons.lang3);
importPackage(Packages.com.winning.mirth);
importPackage(Packages.com.bazaarvoice.jolt);
importPackage(Packages.com.mirth.connect.userutil);


try {

    // 获取全局的数据库配置参数，存储过程在日间手术

    var hisApiConfig = configurationMap.get('winning.his.sqlserver.api.config');
    //var dbconfig=hisApiConfig['dbconfig'];
    //请求的参数字符串
    var requestStr = connectorMessage.getRawData();
    //打印日志
    // logger.info('获取检查检验报告申请单信息调用请求的参数是===='+requestStr);
    // 转换格式，目前不转换，备用
    var spec = [{
            "operation": "shift",
            "spec": {
                "patientId": "patientId",
                "startTime": "startTime",
                "endTime": "endTime"
            }
        },
        {
            "operation": "default",
            "spec": {
                "patientId": "",
                "startTime": "",
                "endTime": ""
            }
        }
    ]

    //通过规则转换进行请求参数的处理
    var jsonData = JSON.parse(WinningJoltUtil.transform(JSON.parse(requestStr), spec));

    //存储过程名称[usp_asmc_getReportByPatid]
    var procName = 'usp_asmc_getInfectiousDiseaseResult';
    //存储过程的参数拼接
    var inArgInfo = WinningCommonUtil.toMap(JSON.stringify(jsonData));
    var patientId = inArgInfo['patientId'];
    if (StringUtils.isEmpty(patientId)) {
        var output = Maps.map().add('status', false).add('msg', '患者[patientId]不能为空');
        var msg = JsonUtils.toJsonString(output);
        responseMap.put('ACK', msg);
    } else {
        var list = WinningSpUtils.callSpsGetList(hisApiConfig, procName, inArgInfo);
        // var retJsonStr=WinningCommonUtil.listToJsonStr(list);
        //若需要把数据库字段转换字段，执行下面代码,
        var repsoneSpec = [{
                "operation": "shift",
                "spec": {
                    "*": {
                        "xmdm": "data[&1].projectCode",
                        "xmjg": "data[&1].result",
                        "xmmc": "data[&1].projectName"
                    }
                }
            },
            {
                "operation": "default",
                "spec": {
                    "data[]": {
                        "*": {
                            "statusDict": "1"
                        }
                    }
                }
            }

        ];
        // 如果需要转换则使用固定格式处理
        var winResponseStr = WinningJoltUtil.transform(list, repsoneSpec);
        responseMap.put('ACK', winResponseStr);
    }
} catch (e) {
    var output = Maps.map().add('status', false).add('msg', '查询异常' + e);
    var msg = JsonUtils.toJsonString(output);
    responseMap.put('ACK', msg);
}

