importPackage(Packages.java.util);
importPackage(Packages.com.winning.mirth);
importPackage(Packages.com.bazaarvoice.jolt);

//获取日间请求的参数，用于后面的参数拼接
var originalRequestStr = connectorMessage.getRawData();

var requestObj = JSON.parse(originalRequestStr);

try {
    // 获取全局的配置参数，存储过程在日间手术
    var url360 = configurationMap.get('patient.360.url');
    if (requestObj) {
        requestObj['userCode'] = requestObj['operatorCode'];
        var tempUrl = url360 + "/winsso/c/${userCode}/${patientIdNo}/1/${patientId}/${outpatientId}/0/0/${outpatientMedicalNo}/-1/42505626100/0/${outpatientDeptName}/mzysclient";
        var jump360url = WinningCommonUtil.replace(requestObj, tempUrl);
        var output = Maps.map().add('status', true).add('msg', '操作成功').add('count', 1).add('data', jump360url);
        msg = JsonUtils.toJsonString(output);
        responseMap.put('ACK', msg);
    }

} catch (e) {
    logger.error('获取360token异常====' + e);
    var output = Maps.map().add('status', false).add('msg', '查询异常' + e);
    var msg = JsonUtils.toJsonString(output);
    responseMap.put('ACK', msg);
}

