                                "@(2,emrSetId)": "emrSetId"
                            }
                        }
                    }
                }
            }
        }];

        var jsonData = WinningJoltUtil.transform(repJson, spec);
	   if (jsonData != null && jsonData !== undefined) {
		    var jsonDataObj = JSON.parse(jsonData);

		    if (jsonDataObj != null && jsonDataObj !== undefined) {
		        var emrSetId = jsonDataObj['emrSetId'];

		        if (emrSetId != null && emrSetId !== undefined) {
		              channelMap.put("emrSetId", emrSetId);
		        }
		    } else {
				var output=Maps.map().add('status',false).add('msg','查询数据为空').add('count',1).add('data',null);	
		        msg= JsonUtils.toJsonString(output); 
		        responseMap.put('ACK',msg);
		    }
		} else {

			  var output=Maps.map().add('status',false).add('msg','查询数据为空').add('count',1).add('data',null);	
		       msg= JsonUtils.toJsonString(output); 
		       responseMap.put('ACK',msg);
		}





    }

} catch (e) {

	logger.info('查询麻醉评估单异常'+e);

}

